import React from 'react'
import './App.css';
import mainLogo from'./image/logo_V2.png';

function DisplayImage (props) {
  return (
    <img src={props.src} alt={props.alt} className={props.class}/>
  )
 }

function DisplayIcon (props) {
  return (
    <i className={props.IclassName}></i>
  )
}
function NavBar (props) {
  const {ulClassName, liClassName, link} = props;
  const features = ["Notre service", "Système de consigne", "Nous contacter"]; 
  console.log(props.array.isDisplay);
  return (
    <nav>
      {/* <DisplayIcon IclassName="fa-solid fa-xmark"/> */}
      <i className="fa-solid fa-xmark" onClick={(props) => props.array.setDisplay(!array.isDisplay)}/>
      <ul className={ulClassName}>
        {features.map((feature, index) => <li key={index}><a href={link} className={liClassName}>{feature}</a></li>)}
      </ul>
    </nav>
  )
}

function Header () {
  const [isDisplay, setDisplay] = React.useState(false);
  return (
    <header className="header">
      <DisplayImage src={mainLogo} alt="mainLogo" class="mainLogo"/>
      <i className="fa-solid fa-bars" onClick={() => setDisplay(!isDisplay)}/>
      {/* <DisplayIcon IclassName="fa-solid fa-bars" array={[isDisplay, setDisplay]}/> */}
      {isDisplay && <NavBar ulClassName="navBar" liClassName="feature" link="#" array={[isDisplay, setDisplay]}/>}
    </header>
  )
}

function App() {
  return (
    <div className="container">
      <Header/>
    </div>
  )

}

export default App;


